from datetime import *import calendarclass Calendar:    def GetCurrentDate(self):        return datetime.now()        def GetWeekDay(date):        if(type(date) is not datetime):            date = GetCurrentDate()            return weekDays[date.weekday()]        def PrintWeekDays(self, short):        if(short):            for d in self.weekDays:                print(d[0:3], end = '\t')        else:            for d in self.weekDays:                print(d, end = '\t')        print()        def GetFirstDay(self, date):        if(type(date) is not datetime):            date = GetCurrentDate()            return date - timedelta(days = date.weekday())        def GetFirstDayOfMonth(self, date):        if(type(date) is not datetime):            date = GetCurrentDate()            return date - timedelta(days = date.day-1)        def GetListOfDates(self, date, count = 7):        dateList = []        for d in range(0, count):            dateList.append(date + timedelta(days = d))            return dateList        def PrintWeek(self, date, showDifferentMonth = True):        if(type(date) is not datetime):            date = self.GetCurrentDate()        self.PrintWeekDays(True)        firstDay = self.GetFirstDay(date)        for d in range(0, firstDay.weekday()):            print('\t', end = '')        counter = firstDay.weekday()        for d in self.GetListOfDates(self.GetFirstDay(date)):            if(showDifferentMonth or d.month == date.month):                print(d.day, end = '\t')                counter += 1                if(counter % 7 == 0):                    print()        def PrintMonth(self, date):        if(type(date) is not datetime):            date = self.GetCurrentDate()                self.PrintWeekDays(True)        firstDay = self.GetFirstDayOfMonth(date)        for d in range(0, firstDay.weekday()):            print('\t', end = '')        counter = firstDay.weekday()        for d in self.GetListOfDates(firstDay, calendar.monthrange(date.year, date.month)[1]):            print(d.day, end = '\t')            counter += 1            if(counter % 7 == 0):                print()        print()    def PrintYear(self, date):        if(type(date) is not datetime):            date = self.GetCurrentDate()                for m in range(1, 13):            print(self.months[m-1])            self.PrintMonth(datetime(date.year, m, 1))            print()        weekDays = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday']    months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December']cal = Calendar()cal.PrintYear(0)